import React from "react";
import { useState } from "react";

const Signup=()=>{
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const CollectData=async()=>{
        console.log(name,email,password);
        let result = await fetch("http://localhost:5000/register",{
            method: 'POST',
            mode: "same-origin",
            body: JSON.stringify({name,email,password}),
            headers: {
                'Content-Type': 'application/json'
            }
        });
        result = await result.json();
        console.warn(result);
    }
    
    return(
        <div className="register">
            <h1>Signup</h1>
            <input className="inputBox" type="text" placeholder="Enter Full Name"
            value={name} onChange={(e) => setName(e.target.value)}/>

            <input className="inputBox" type="text" placeholder="Enter Email ID "
            value={email} onChange={(e) => setEmail(e.target.value)}/>

            <input className="inputBox" type="password" placeholder="Password"
            value={password} onChange={(e) => setPassword(e.target.value)}/>

            <button onClick={CollectData} className="SignUpButton" type="button">SignUp</button>
        </div>
    )
}
export default Signup;
